import { Link, useLocation } from 'react-router-dom';
import { Home, Camera, Video, Palette, MessageSquare, Users, Package, Menu, X } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';
import { useState } from 'react';

const Sidebar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const location = useLocation();

  const navItems = [
    { path: '/', icon: <Home size={20} />, label: 'Home' },
    { path: '/photography', icon: <Camera size={20} />, label: 'Photography' },
    { path: '/videography', icon: <Video size={20} />, label: 'Videography' },
    { path: '/graphic-design', icon: <Palette size={20} />, label: 'Graphic Design' },
    { path: '/feedback', icon: <MessageSquare size={20} />, label: 'Feedback' },
    { path: '/featured-freelancers', icon: <Users size={20} />, label: 'Featured Freelancers' },
    { path: '/starter-kit', icon: <Package size={20} />, label: 'Starter Kit' },
  ];

  const sidebarVariants = {
    open: {
      x: 0,
      transition: {
        type: "spring",
        stiffness: 300,
        damping: 30
      }
    },
    closed: {
      x: "-100%",
      transition: {
        type: "spring",
        stiffness: 300,
        damping: 30
      }
    }
  };

  const menuButton = (
    <button
      onClick={() => setIsOpen(!isOpen)}
      className="fixed top-4 left-4 z-50 p-2 rounded-lg bg-gray-800 text-white md:hidden"
    >
      {isOpen ? <X size={24} /> : <Menu size={24} />}
    </button>
  );

  const sidebar = (
    <motion.div
      variants={sidebarVariants}
      initial="closed"
      animate={isOpen ? "open" : "closed"}
      className="fixed md:static w-64 h-screen bg-gray-800 text-white p-4 z-40"
    >
      <motion.div
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.2 }}
        className="text-2xl font-bold mb-8"
      >
        KreatorNest
      </motion.div>
      <nav>
        <ul className="space-y-2">
          {navItems.map((item, index) => (
            <motion.li
              key={item.path}
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ delay: index * 0.1 }}
            >
              <Link
                to={item.path}
                className={`flex items-center space-x-2 p-3 rounded-lg transition-all duration-300 ${
                  location.pathname === item.path
                    ? 'bg-blue-600 text-white'
                    : 'hover:bg-gray-700'
                }`}
                onClick={() => setIsOpen(false)}
              >
                {item.icon}
                <span>{item.label}</span>
              </Link>
            </motion.li>
          ))}
        </ul>
      </nav>
    </motion.div>
  );

  return (
    <>
      {menuButton}
      <AnimatePresence>
        {sidebar}
      </AnimatePresence>
    </>
  );
};

export default Sidebar;
